"""
This script will calculate Ka/Ks values for a given two sequences as arguments.
Requirements: kaks_calculator2
"""

import sys
import pandas as pd
import os
import time

def compute_kaks_ng(nt_seq1, nt_seq2):

    # check if the command "KaKs_Calculator -h" works
    if os.system('KaKs_Calculator -h'  + ' > /dev/null 2>&1') != 0:
        raise ValueError("KaKs_Calculator is not installed or not in the PATH.")
    
    if len(nt_seq1) != len(nt_seq2):
        raise ValueError("Input sequences must be the same length.")
    
    if len(nt_seq1) % 3 != 0:
        raise ValueError("Input sequences must be a multiple of 3.")
    
    # Write the sequences to a temporary axt file with name query
    with open('query.axt', 'w') as f:
        f.write('query\n')
        f.write(nt_seq1 + '\n')
        f.write(nt_seq2 + '\n')
        f.close()

    # Run kaks_calculator2
    cmd = 'KaKs_Calculator -i query.axt -o query.kaks -m NG'
    # run command in silent mode
    os.system(cmd + ' > /dev/null 2>&1')

    # Read the output file as a pandas dataframe
    df = pd.read_csv('query.kaks', sep='\t')

    # Get the Ka, Ks, and Ka/Ks values
    ka = df['Ka'][0]
    ks = df['Ks'][0]
    kaks = df['Ka/Ks'][0]

    return ka, ks, kaks


if __name__ == '__main__':
    # a simple test to see if the function works
    seq1 = ''
    seq2 = ''

    i = 0
    start_time = time.time()
    while i < 100:
        print(compute_kaks_ng(seq1, seq2))
        i += 1
    end_time = time.time()
    print('Time for each iteration:', (end_time-start_time)/100)
